mapper.select:
SELECT A.NOTICE_NO BOARD_NO, NOTICE_TITLE BOARD_TITLE, NOTICE_CONTENT BOARD_CONTENT , NOTICE_DATE BOARD_DATE, NOTICE_HIT BOARD_HIT , FILE_NO, FILE_NAME, FILE_PATH FROM NOTICE A LEFT OUTER JOIN NOTICEATTACHMENT B ON(A.NOTICE_NO = B.NOTICE_NO) WHERE A.NOTICE_NO IN( TO_NUMBER(#{notice_no}),
(SELECT MAX(NOTICE_NO) FROM NOTICE WHERE NOTICE_NO < TO_NUMBER(#{notice_no})), (SELECT MIN(NOTICE_NO) FROM NOTICE WHERE NOTICE_NO > TO_NUMBER(#{notice_no}))
) ORDER BY TO_NUMBER(A.NOTICE_NO)

mapper.update:
UPDATE NOTICE
SET NOTICE_HIT = TO_NUMBER(NOTICE_HIT) + 1
WHERE NOTICE_NO = #{notice_no}
