mapper.select:
SELECT COUNT(*)
FROM REPLY
WHERE BOARD_NO = #{board_no}

SELECT USER_ID, USER_NAME, USER_PROFILE_PATH
FROM USERS
WHERE USER_ID = #{user_id}

mapper.insert:
INSERT INTO REPLY(
	BOARD_NO, LECTURE_CODE, REPLY_NO, ATTEND_NO
	, REPLY_CONTENT, REPLY_REG
) VALUES (
	#{board_no}, #{lecture_code}, REPLY_SEQ.NEXTVAL, #{attend_no}
	, #{reply_content}, SYSDATE
)

mapper.update:
UPDATE REPLY
SET REPLY_CONTENT = #{reply_content}
WHERE REPLY_NO = #{reply_no}
