		    #{ring_code}, #{ring_response_id}, #{ring_request_id}
		    , #{ring_type}, (SELECT NVL(MAX(TO_NUMBER(RING_NO)),0)+1 FROM RING WHERE RING_RESPONSE_ID = #{ring_response_id})
		    , #{ring_move_address}, SYSDATE, 'N', #{ring_title}
		)
